import React, {useContext, useEffect, useState} from 'react';
import MovieBox from '../MovieBox/MovieBox';
import { observer } from "mobx-react-lite"
import Store from '../../mobx/store'


const MovieList = () => {
    const startState = {
        title: "",
        year: 0,
        genres: [],
        storyline: "",
        posterurl: "",
        imdbRating: 0,
        actors: [],
        rating: 0
    };

    const [numberOfMovies, setNumberOfMovies] = useState(5);
    let movieArray = [[]];
    const [movies, setMovies] = useState(movieArray);
    const store = useContext(Store);
    const { states } = store;

    useEffect( () => {
        if (states[0].search_string) {
            // non-empty search string -> search for the specified title
            console.log("non-empty search string: " + states[0].search_string);

             fetch("http://localhost:3000/api/movies/title/" + states[0].search_string,
                {
                    method: 'GET'
                })
                .then(res => res.json())
                .then(json => {
                    movieArray = [];
                    movieArray.push(json)
                    //movieArray.shift();
                    setMovies(movies);
                })
                .catch(error => {
                    console.log('Could not get movies from DB');
                });
        }
        else {
            // empty search string -> get all movies
            console.log("empty search string");

             fetch("http://localhost:3000/api/movies/",
                {
                    method: 'GET'
                })
                .then(res => res.json())
                .then(json => {
                    movieArray = [];
                    movieArray.push(json)
                    movieArray.shift();
                    setMovies(movies);
                    console.log(movies[0]);

                })
                .catch(error => {
                    console.log('Could not get movies from DB');
                });
        }
    }, []);




    return(
        <div>

        </div>
    )
}


export default observer(MovieList);


