import React, {useContext, useEffect, useState} from 'react';
import MovieBox from '../MovieBox/MovieBox';
import {inject, observer} from "mobx-react"
import Store, {StoreType} from '../../mobx/store'


interface Props {
    store: StoreType;
}

const MovieList = (props: Props) => {
    //const [numberOfMovies, setNumberOfMovies] = useState(5);
    const [movies, setMovies] = useState([]);
    //const store = useContext(Store);
    const { states, getState } = props.store;


    useEffect( () => {
        console.log("Search string is now: " + states.search_string);
        if (states.search_string) {
            // non-empty search string -> search for the specified title
            console.log("non-empty search string: " + states.search_string);

             fetch("http://localhost:3000/api/movies/title/" + states.search_string,
                {
                    method: 'GET'
                })
                .then(res => res.json())
                .then(json => {
                    setMovies(json);
                })
                .catch(error => {
                    console.log('Could not get movies from DB');
                });
        }
        else {
            // empty search string -> get all movies
            console.log("empty search string");

             fetch("http://localhost:3000/api/movies/",
                {
                    method: 'GET'
                })
                .then(res => res.json())
                .then(json => {
                    setMovies(json);
                })
                .catch(error => {
                    console.log('Could not get movies from DB');
                });
        }
    }, [states, getState]);


    return(
        <div>
            { movies.map( movie =>
                <ul key={movie["_id"]}>
                    <MovieBox id={movie["_id"]} title={movie["title"]}
                              duration={movie["duration"]}
                              genres={movie["genres"]}
                              imgUrl={movie["posterurl"]}
                              year={movie["year"]}
                              imdbRating={movie["imdbRating"]}
                              store={props.store}
                    />
                </ul>
            )}
        </div>
    )
}


export default observer(MovieList);
