import React, {useContext, Component, useState } from 'react';
import MovieBox from '../MovieBox/MovieBox';
import { observer } from "mobx-react-lite"
import Store from '../../mobx/store'


const [numberOfMovies, setNumberOfMovies] = useState(5);
const store1 = useContext(Store);
const { states } = store1;

const MovieList = () => {
    let movies = fetch("localhost:3000/api/movies/title/" + states[0].search_string)
        .then( res => res.json());

    console.log("Search state: " + states[0].search_string)

    return(
        <div></div>
