import React, {useContext, useEffect, useState} from 'react';
import './MovieInfo.css';
import { observer } from "mobx-react-lite"
import Store from '../../mobx/store'

interface Movie {
    title: "";
    duration: 0;
    genres: [],
    year: 0,
    actors: [],
    storyline: "",
    imdbRating: 0
}

const MovieInfo = () => {
    const [id, setId] = useState("");
    const [movie, setMovie] = useState();
    const store = useContext(Store);
    const { select_id } = store;
    let duration = "Unknown";
    let imdbRating = "Unknown";

    useEffect( () => {
        fetch("http://localhost:3000/api/movies/title/Game Night")
            .then( res => res.json())
            .then( mov => setMovie(mov))
            .catch( error => {
                console.log('Could not get selected movie from DB')
            });
    });


    useEffect( () => {
        setId(select_id);

        fetch("http://localhost:3000/api/movies/" + id)
            .then( res => res.json())
            .then( mov => setMovie(mov))
            .catch( error => {
                console.log('Could not get selected movie from DB');
            });

        duration = movie[0]["duration"]
        duration = duration.slice(2);
        duration = duration.substring(0, duration.length - 1);
        duration += " min";

        if (movie[0]["imdbRating"]) {
            imdbRating = movie[0]["imdbRating"];
            imdbRating = imdbRating.toString();
            imdbRating += "/10";
        }
    }, [select_id, movie]);


    return (
        <div id="movieInfo">
            <h3 id={movie[0]["_id"]}>{movie[0]["title"]}</h3>
            <hr/>
            <p className="movieInfo">Year: {movie[0]["year"]}</p>
            <p className="movieInfo">Duration: {duration}</p>
            <p className="movieInfo">Genres: {movie[0]["genres"].join(', ')}</p>
            <p className="movieInfo">Main actors: {movie[0]["actors"].join(', ')}</p>
            <p className="movieInfo">Storyline: {movie[0]["storyline"]}</p>
            <p className="movieInfo">IMDB rating: {imdbRating}</p>
        </div>
    )
}

export default observer(MovieInfo);
