import React, {useContext, useState } from 'react';
import MovieBox from '../MovieBox/MovieBox';
import { observer } from "mobx-react-lite"
import Store from '../../mobx/store'


const MovieList = () => {
    const [numberOfMovies, setNumberOfMovies] = useState(5);
    const store = useContext(Store);
    const { states } = store;

    if (states[0].search_string === "") {
        // get all movies
        let movies = fetch("localhost:3000/api/movies/")
            .then( res => res.json());
    }
    else {
        // search for the specified title
        let movies = fetch("localhost:3000/api/movies/title/" + states[0].search_string)
            .then( res => res.json());
    }


    console.log("Search state: " + states[0].search_string)

    return(
        <div></div>
