import React, {useContext, useEffect, useState} from 'react';
import './MovieInfo.css';
import { observer } from "mobx-react-lite"
import Store from '../../mobx/store'

const initialMovie = {
    _id: "",
    title: "",
    duration: "",
    genres: ["unknown", "unknown"],
    year: "",
    actors: ["", ""],
    storyline: "",
    imdbRating: "",
    posterurl: ""
}

const MovieInfo = () => {
    const store = useContext(Store);
    const { select_id } = store;
    const [id, setId] = useState("5f897e75987fae2bf4a18131");
    const [movie, setMovie] = useState(initialMovie);

    let duration = "Unknown";
    let imdbRating = "Unknown";


    useEffect( () => {
        setId(select_id);

        fetch("http://localhost:3000/api/movies/" + id)
            .then( res => res.json())
            .then( mov => setMovie(mov))
            .catch( error => {
                console.log('Could not get selected movie from DB');
            });

        duration = movie["duration"]
        duration = duration.slice(2);
        duration = duration.substring(0, duration.length - 1);
        duration += " min";

        if (movie["imdbRating"]) {
            imdbRating = movie["imdbRating"];
            imdbRating = imdbRating.toString();
            imdbRating += "/10";
        }
    }, [select_id, movie]);


    return (
        <div id="movieInfo">
            <h3 id={movie["_id"]}>{movie["title"]}</h3>
            <hr/>
            <img id={movie["_id"]} src={movie["posterurl"]} alt="Missing poster" width="200" height="300"/>
            <p className="movieInfo">Year: {movie["year"]}</p>
            <p className="movieInfo">Duration: {duration}</p>
            <p className="movieInfo">Genres: {movie["genres"].join(', ')}</p>
            <p className="movieInfo">Main actors: {movie["actors"].join(', ')}</p>
            <p className="movieInfo">Storyline: {movie["storyline"]}</p>
            <p className="movieInfo">IMDB rating: {imdbRating}</p>
        </div>
    )
}

export default observer(MovieInfo);
